import React, { useEffect, useState } from "react";
import NewCommentForm from "./NewCommentForm";
import CommentCard from "./CommentCard";
import CommentApi from "..//../api/CommentApi";

function CommentsPage() {
    const [comments, setComments] = useState([]);

 //   const createComment = (commentData)=>{
 //       CommentApi.createComment(commentData)
 //           .then((response)=> console.log(response));
  //      };    

  const createComment = async (e)=>{
     try {
        e.preventDefault();
        const response = await CommentApi.createComment("commentData");
        console.log(response);
     } catch (error) {
         console.log(error)
     }
    }; 



    const getAll = () => {
        CommentApi.getAllComments("/comments")
        .then(response => setComments(response.data));
    }

    const deleteComment = (comment) => {
        CommentApi.deleteComment("/comments" + comment.id)
            .then(response => getAll());
    }

    const updateComment = (updatedComment) => {
        CommentApi.updateComment("/comments", updatedComment)
            .then(r => getAll());
    }

    useEffect(()=>{
        getAll();
    },[])


    return (
        <div>
            <NewCommentForm onSubmit={createComment} />
            {
                comments.map(comment => (<CommentCard
                    comment={comment}
                    onUpdateClick={updateComment}
                    onDeleteClick={deleteComment} />))
            }
        </div>
    )

}

export default CommentsPage;
